import { plantList } from '../datas/plantList'
import '../styles/ShoppingList.css'
import '../styles/PlantItem.css'
import PlantItem from './PlantItem.js'
import imagePlant from '../assets/monstera.jpg'


/*
function categoryList(plant, index, array){
    let already = 0;
    for(let i=0; i<index; i++){
        if(plant.category === array[i].category){
            already = 1;
            break;
        }
    }
    if(already === 0){
        return (<li key={plant.id}>{ plant.category }</li>)
    }
}
*/

function ShoppingList() {
    const categories = plantList.reduce(
		(acc, plant) =>
			acc.includes(plant.category) ? acc : acc.concat(plant.category),
		[]
	)
    /*console.log(1)*/
    return (
        <div>
			<ul>
				{categories.map((category) => (
					<li key={category}>{category}</li>
				))}
			</ul>
			<ul className='lmj-plant-list'>
				{plantList.map((plant) => (
					<li key={plant.id}>
                        {plant.hasOwnProperty('isSpecialOffer') && plant.isSpecialOffer && <div className='lmj-sales'>Soldes</div>}
                        <PlantItem name={plant.name} cover='imagePlant' id={plant.id} light={plant.light} water={plant.water} />
                    </li>
				))}
			</ul>
		</div>
    )
}

export default ShoppingList
